    private void cbxSRMDActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbxSRMDActionPerformed
       String Calcula = cbxSRMD.getSelectedItem().toString();
String Operador = "";
if ( Calcula.equals("Suma")) {
    Operador = "+";
} else if ( Calcula.equals("Resta")) {
   Operador = "-";
} else if ( Calcula.equals("Multiplicacion")) {
    Operador = "*";
} else if ( Calcula.equals("Division")) {
    Operador = "/";
}
lblSignos.setText(Operador);
    }//GEN-LAST:event_cbxSRMDActionPerformed

