   private String Calcu() {
    String Calcu = cbxSRMD.getSelectedItem().toString();
    int Num1 = Integer.parseInt(txtNum1.getText());
    int Num2 = Integer.parseInt(txtNum2.getText());
    String resultado = "";

    if (Calcu.equals("Suma")) {
        resultado = String.valueOf(Num1 + Num2);
    } else if (Calcu.equals("Resta")) {
        resultado = String.valueOf(Num1 - Num2);
    } else if (Calcu.equals("Multiplicacion")) {
        resultado = String.valueOf(Num1 * Num2);
    } else if (Calcu.equals("Division")) {
        if (Num2 != 0) {
            resultado = String.valueOf(Num1 / Num2);
        } else {
            resultado = "Infinity";
        }
    }

    txtOperacion.setText(resultado);
    return resultado;
}


